import { Button, Grid, jssPreset, makeStyles, TextField, TextareaAutosize } from '@material-ui/core'
import axios from 'axios'
import React from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'

function MyServingsList() {

    const [servings, setServings] = React.useState([])
    const [enabled, setEnabled] = React.useState(true)
    const URL = 'http://127.0.0.1:8000'
    const store = useSelector(state => state.userReducer?.user ? state.userReducer?.user : state.userReducer?.user?.user)
    const user = JSON.parse(localStorage.getItem('userInfo'))
    const myServings = async () => {

        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }

        const { data } = await axios.get(`${URL}/api/restaurant/my-servings/`, config)
        setServings(data)
    }

    React.useEffect(() => {
        setTimeout(() => {
            myServings()
        }, 500)
    }, [setServings])



    const useStyles = makeStyles(theme => ({
        title: {
            ...theme.typography.content,
            textAlign: 'center',
            fontSize: '2.5rem',
            marginTop: '1rem',
            marginBottom: '30px'
        },
        main: {
            width: '13vw',
            minWidth: '250px',
            borderColor: theme.palette.primary.light,
            borderRadius: '10px',
            border: '3px solid',
            marginTop: '30px',
            backgroundColor: 'white',
            marginRight: 'auto',
            marginLeft: 'auto',
            textAlign: 'center',
            boxShadow: '0px 1px 5px grey',
            paddingBottom: '20px',
            display:'flex',
            flexDirection:'column'
        },
        name: {
            ...theme.typography.content,
            marginTop: '10px',
            fontSize: '2rem'
        },
        image: {
            height: '150px',
            width: '14vw',
            minWidth: '130px',
            backgroundColor: 'grey',
            marginRight: 'auto',
            marginLeft: 'auto',
            marginTop: '10px',
            borderRadius: '5px'
        },
        description: {
            ...theme.typography.title,
            marginTop: '10px'
        },
        span: {
            fontWeight: '900'
        },
        ing: {
            ...theme.typography.title
        },
        btn: {
            ...theme.buttonDef,
            marginTop: '20px',
            width:'50%',
            right:'0px'
        }
    }))
    const classes = useStyles()



    return (
        <div>
            <h1 className={classes.title} >My servings list</h1>
            <Grid container justify='center' >
                {servings?.map(item => {
                    
                    const onEditClickHandler = () => {
                        setEnabled(false)
                    }
                    console.log(item)
                    return (
                        <Grid item xs={12} sm={6} md={4} direction='row' className={classes.main} >
                            <img src={`http://127.0.0.1:8000${item.image}`}
                                alt={item.image} className={classes.image} ></img>
                            <TextField className={classes.name} value={item.name} disabled={enabled} />
                            <TextareaAutosize className={classes.description} value={item.description} />
                            <TextField className={classes.ing} value={item.ingredients} disabled={enabled} />
                            <Button variant='outlined' color='primary' onClick={onEditClickHandler}
                                className={classes.btn} >Edit</Button>
                        </Grid>
                    )
                })}
            </Grid>
        </div>
    )
}

export default MyServingsList
