import { Button, Grid, jssPreset, makeStyles, TextField, TextareaAutosize } from '@material-ui/core'
import axios from 'axios'
import React from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import MyServing from '../../components/MyServing'

function MyServingsList() {

    const [servings, setServings] = React.useState([])
    const [enabled, setEnabled] = React.useState(true)
    const URL = 'http://127.0.0.1:8000'
    const store = useSelector(state => state.userReducer?.user ? state.userReducer?.user : state.userReducer?.user?.user)
    const user = JSON.parse(localStorage.getItem('userInfo'))
    const myServings = async () => {

        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }

        const { data } = await axios.get(`${URL}/api/restaurant/my-servings/`, config)
        setServings(data)
    }

    React.useEffect(() => {
        setTimeout(() => {
            myServings()
        }, 500)
    }, [setServings])



    const useStyles = makeStyles(theme => ({
        title: {
            ...theme.typography.content,
            textAlign: 'center',
            fontSize: '2.5rem',
            marginTop: '1rem',
            marginBottom: '30px'
        },
        main: {
            width: '13vw',
            minWidth: '250px',
            borderColor: theme.palette.primary.light,
            borderRadius: '10px',
            border: '3px solid',
            marginTop: '30px',
            backgroundColor: 'white',
            marginRight: 'auto',
            marginLeft: 'auto',
            textAlign: 'center',
            boxShadow: '0px 1px 5px grey',
            paddingBottom: '20px',
            display: 'flex',
            flexDirection: 'column'
        },
        
    }))
    const classes = useStyles()



    return (
        <div>
            <h1 className={classes.title} >My servings list</h1>
            <Grid container justify='center' >
                {servings?.map(item => {
                    
                    return (
                        <MyServing/>
                    )
                })}
            </Grid>
        </div>
    )
}

export default MyServingsList
