import { Grid, TextField, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles'
import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { RestaurantServings } from '../../actions/RestaurantActions';

function RestProfileScreen(props) {

    const [textFieldDisable,setTextFieldDisable] = React.useState(true)

    const dispatch = useDispatch()

    const restInfo = JSON.parse(localStorage.getItem('userInfo'))
    console.log(restInfo)
    React.useEffect(() => {
        dispatch(RestaurantServings(JSON.parse(localStorage.getItem('userInfo')).id))
        if (props.match.params.id != restInfo.id) {
            props.history.push('/rest/profile/' + restInfo.id)
        }
    }, [dispatch])


    const useStyles = makeStyles(theme => ({
        heading: {
            ...theme.typography.content,
            fontSize: '2.5rem',
            textAlign: 'center'
        },
        formDiv: {
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            height: '400px',
            width: '400px',
            backgroundColor: theme.palette.secondary.light,
            marginTop:'30px',
            borderRadius:'15px'
        },
        label:{
            ...theme.typography.title
        },
        textField:{
            paddingLeft:'10px'
        }
    }))
    const classes = useStyles()
    return (
        <div style={{
            display: 'flex',
            flexDirection:'column',
            alignItems:'center'
        }} >
            <h1 className={classes.heading} >My servings</h1>
            <div className={classes.formDiv} >
                <div style={{ display: 'flex', alignItems: 'center' }} >
                    <Typography className={classes.label} >Name :</Typography>
                    <TextField disabled={textFieldDisable} className={classes.textField} placeholder={restInfo.name}  />
                </div>
            </div>
            {/* {restaurantServings?.servings?.map(item => {
                return (
                    <Grid xs={12} container >
                        <Grid item xs={12} md={} >

                        </Grid>
                    </Grid>
                )
            })} */}
        </div>
    )
}

export default RestProfileScreen
