import { makeStyles } from '@material-ui/core'
import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RestaurantServings } from '../../actions/RestaurantActions'

function MyServingsList() {
    const dispatch = useDispatch()
    const user = useSelector(state => state.userReducer?.user ? state.userReducer?.user : state.userReducer?.user?.user)
    const id = user?.user?.id ? user?.user?.id : user?.id 
    const myservings = useSelector(state=>state.restaurantServings.servings)


    React.useEffect(()=>{
        if(id){
            dispatch(RestaurantServings(id))
        }
    },[dispatch])
    const useStyles = makeStyles(theme => ({
        title: {
            ...theme.typography.content,
            textAlign: 'center',
            fontSize: '2.5rem',
            marginTop: '1rem',
            marginBottom: '30px'
        }
    }))
    const classes = useStyles()



    return (
        <div>
            <h1 className={classes.title} >My servings list</h1>
            {myservings?.map(item=>{
                return(
                    <div>
                        {item.name}
                    </div>
                )
            })}
        </div>
    )
}

export default MyServingsList
