import { Button, Container, Grid, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import { motion } from 'framer-motion';
import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import apple from '../allImages/apple.png';
import arrow from '../allImages/arrow.png';
import banner from '../allImages/banner.jpg';
import logo from '../allImages/logo.png';
import restaurant from '../allImages/restaurant.png';
import truck from '../allImages/truck.png';
import Box from '../components/Box';
import Footer from '../components/Footer';
import Section from '../components/Section';
import { defaultTransition, pageTransition } from '../PageTransitions';



function HomeScreen() {

    const transition = useSelector(state=>state.transitions)

    const useStyles = makeStyles(theme => ({
        button: {
            ...theme.buttonDef,
            marginRight: '50px',
        },
        navBar: {
            display: 'flex',
            justifyContent: 'space-between',
            [theme.breakpoints.down('xs')]: {

            }
        },
        navButtons: {
            marginTop: '60px',
            marginRight: '180px',
            display:'flex'
        },
        mainTitle: {
            ...theme.importantHeader,
            color: theme.palette.common.white,

        },
        ctaBtn: {
            ...theme.buttonDef,
            marginTop: '50px'
        },
        sectionDiv: {
            textAlign: 'center'
        },
        apple: {
            hieght: '302px',
            width: '302px'
        },
        title: {
            ...theme.typography.content,
            fontSize: '59px',

        },
    }))

    const classes = useStyles()

    return (
        <motion.div
            initial={defaultTransition.out}
            animate={defaultTransition.in}
            exit={defaultTransition.out}
            transition={pageTransition}
            variants={defaultTransition}
        >
            <div className='main' >
                <img src={banner} className='main-image' alt='' ></img>
                <div className={classes.navBar} >
                    <img src={logo} className='logo' alt='' ></img>
                    <div className={classes.navButtons} >
                        <motion.div
                            animate={{opacity:1,x:0}}
                            transition={{ease:'easeOut',duration:1,delay:0.8}}
                        ><Button variant='contained' color='primary' className={classes.button}  >Login as Restaurant</Button></motion.div>
                        <motion.div><Button variant='contained' color='primary' className={classes.button} >Login as User</Button></motion.div>
                    </div>
                </div>
                <Grid
                    container
                    direction='row'
                    justify='center'
                    alignItems='center'

                >
                    <Grid container xs={0} md={6}></Grid>
                    <Grid
                        container
                        md={6}
                        style={{ display: 'flex', paddingRight: '150px' }}
                        direction='column'
                        justify='center'
                        alignItems='center'
                    // style={{marginRight:'10px'}}
                    >
                        <Container style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            textAlign: 'center',
                            flexDirection: 'column'
                        }} >
                            <Typography variant='h2' className={classes.mainTitle} >Because Everyone </Typography>
                            <Typography variant='h2' className={classes.mainTitle} >Deserves Free</Typography>
                            <Typography variant='h2' className={classes.mainTitle} >Food</Typography>
                        </Container>
                        <Button className={classes.ctaBtn} component={Link} to='restaurants' variant='contained' color='primary' >View Restaurants</Button>
                    </Grid>
                </Grid>

            </div>
            <Section
                title='OUR MOTIVE'
                content='We beleive that food must be free for all, and we see a vision where every individual on earth doesnt sleep a night with hunger.'
            />
            <Section
                title='OUR PLAN'
                content='We plan on taking the food from the ones who have
                 excess and give it to the needy ones.Since we are taking this
                 initiative for public service, there will be no charges from the donator or the receiver !'
            />
            <div style={{
                textAlign: 'center',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center'
            }}>
                <img src={apple} alt='apple' className={classes.apple} ></img>
            </div>
            <Container style={{ textAlign: 'center', marginTop: '169px' }} >
                <Typography variant='p' className={classes.title}  >
                    OUR SERVICES
                </Typography>
                <Grid container >
                    <Grid direction='column' xs={12} md={5} lg={3} >
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'space-between',
                            paddingLeft: '50px'
                        }} >
                            <div>
                                <Box
                                    image={truck}
                                    content='Pick up from the Donator'
                                />
                            </div>
                        </div>
                    </Grid>
                    <Grid xs={1} >
                        <div style={{ marginTop: '200px', marginLeft: '40px' }} >
                            <img src={arrow} alt='arrow' ></img>
                        </div>
                    </Grid>
                    <Grid direction='column' xs={12} md={5} lg={3} >
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'space-between',
                            paddingLeft: '50px'
                        }} >
                            <div>
                                <Box
                                    image={restaurant}
                                    content='Store it in a restaurant'
                                />
                            </div>

                        </div>
                    </Grid>
                    <Grid xs={1} >
                        <div style={{ marginTop: '200px', marginLeft: '40px' }} >
                            <img src={arrow} alt='arrow' ></img>
                        </div>
                    </Grid>
                    <Grid direction='column' xs={12} md={6} lg={4} >
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'space-between',
                            paddingLeft: '50px'
                        }} >
                            <div>
                                <Box
                                    image={restaurant}
                                    content='Pick up from the Donator'
                                />
                            </div>
                        </div>
                    </Grid>
                </Grid>
            </Container>
            <Footer />
        </motion.div>
    )
}

export default HomeScreen
