import {
    Button, ClickAwayListener,
    Grid, Grow,
    MenuItem, MenuList,
    Paper, Popper
} from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import React from 'react'
import { useSelector } from 'react-redux'

function DonateServing(props) {
    const restaurantServings = useSelector(state => state.restaurantServings)
    const serving = restaurantServings.servings.find(item => item.id == props.match.params.id)
    const anchorRef = React.useRef(null)
    const [open, setOpen] = React.useState(false);
    const [value,setValue] = React.useState(0)
    console.log(serving)

    const handleToggle = () => {
        setOpen((prevOpen) => !prevOpen);
    };

    const handleClose = (event) => {
        if (anchorRef.current && anchorRef.current.contains(event.target)) {
            return;
        }
        setOpen(false);
        console.log(event.target)
    }

    function handleListKeyDown(event) {
        if (event.key === 'Tab') {
            event.preventDefault();
            setOpen(false);
        }
        
    }

    const useStyles = makeStyles(theme => ({
        dummyImage: {
            height: '45vh',
            width: '25vw',
            minWidth: '430px',
            minHeight: '25vh',
            marginRight: 'auto',
            marginLeft: '50px',
            marginTop: '90px',
            borderRadius: '5px'
        },
        title: {
            ...theme.typography.content,
            color: '#333333',
            textAlign: 'center',
            fontSize: '3.5rem',
            marginBottom: '30px'
        },
        infoDiv: {
            marginTop: '50px',
            marginBottom: '50px'
        },
        info: {
            ...theme.typography.title,
            fontSize: '2rem',
            textAlign: 'center',
            marginBottom: '30px',
            lineHeight: '52px',

        },
        btn: {
            ...theme.buttonDef
        },
        btnDiv: {
            display: 'flex',
            justifyContent: 'space-between',
            paddingLeft: '30px',
            paddingRight: '30px'
        }
    }))

    const classes = useStyles()


    return (
        <Grid container xs={12}>
            <Grid item direction='column' xs={12} md={5} >
                <img src={`http://127.0.0.1:8000${serving?.image}`} alt='image' className={classes.dummyImage} >
                </img>
            </Grid>
            <Grid item direction='coulmn' xs={12} md={6} >
                <div className={classes.infoDiv} >
                    <h1 className={classes.title} >{serving?.name}</h1>

                    <p className={classes.info} >{serving?.description}</p>

                    <p className={classes.info} >Ingredients : {serving?.ingredients}</p>

                    <div className={classes.btnDiv} >
                        <p className={classes.info} >Donate : </p>
                        <Button
                            ref={anchorRef}
                            aria-controls={open ? 'menu-list-grow' : undefined}
                            aria-haspopup="true"
                            variant='outlined'
                            color='primary'
                            onClick={handleToggle}
                        >
                            {value}
                            </Button>
                        <Popper open={open} anchorEl={anchorRef.current} role={undefined} transition disablePortal>
                            {({ TransitionProps, placement }) => (
                                <Grow
                                    {...TransitionProps}
                                    style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }}
                                >
                                    <Paper>
                                        <ClickAwayListener onClickAway={handleClose}>
                                            <MenuList autoFocusItem={open} id="menu-list-grow" onKeyDown={handleListKeyDown}>
                                                <MenuItem onClick={handleClose}>0</MenuItem>
                                                <MenuItem onClick={handleClose}>1</MenuItem>
                                                <MenuItem onClick={handleClose}>2</MenuItem>
                                                <MenuItem onClick={handleClose}>3</MenuItem>
                                                <MenuItem onClick={handleClose}>4</MenuItem>
                                                <MenuItem onClick={handleClose}>5</MenuItem>
                                                <MenuItem onClick={handleClose}>6</MenuItem>
                                                <MenuItem onClick={handleClose}>7</MenuItem>
                                                <MenuItem onClick={handleClose}>8</MenuItem>
                                                <MenuItem onClick={handleClose}>9</MenuItem>
                                                <MenuItem onClick={handleClose}>10</MenuItem>
                                            </MenuList>
                                        </ClickAwayListener>
                                    </Paper>
                                </Grow>
                            )}
                        </Popper>
                    </div>
                </div>
            </Grid>
        </Grid>
    )
}

export default DonateServing
