import axios from 'axios'
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

const URL = 'http://127.0.0.1:8000'

const get_restaurants = createAsyncThunk(URL + '/api/', async () => {
    const response = await get_restaurants()
    return response.data
})

export const counterSlice = createSlice({
    name: 'restaurants',
    initialState: {
        restaurants: [],
        loading: true
    },
    reducers: {

    },
    extraReducers: {
    }
})

export const { get_products } = counterSlice.actions
export default counterSlice.reducer
