import { Grid, TextField } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import axios from 'axios'
import React from 'react'
import { useSelector } from 'react-redux'
import { Switch } from 'react-router'

function MyServingEditList(props) {
    const [servings, setServings] = React.useState([])
    const [state, setState] = React.useState({
        checkedA: true,
        checkedB: true,
    });

    const handleChange = (event) => {
        setState({ ...state, [event.target.name]: event.target.checked });
    };
    const myServings = async () => {
        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }
        const { data } = await axios.get(`${URL}/api/restaurant/my-servings/`, config)
        setServings(data)
    }

    const serving = servings?.find(item => item.id == props.match.params.id)
    console.log(serving)
    const [name, setName] = React.useState(serving?.ingredients)



    console.log(serving?.name)
    const URL = 'http://127.0.0.1:8000'
    // const store = useSelector(state => state.userReducer?.user ? state.userReducer?.user : state.userReducer?.user?.user)
    const user = JSON.parse(localStorage.getItem('userInfo'))


    React.useEffect(() => {

        myServings()

    }, [setServings])

    const useStyles = makeStyles(theme => ({
        image: {
            height: '45vh',
            width: '25vw',
            minWidth: '380px',
            marginLeft: '50px',
            marginTop: '90px',
            borderRadius: '5px'
        },
        title: {
            ...theme.typography.content,
            color: '#333333',
            textAlign: 'center',
            fontSize: '3.5rem',
            marginBottom: '30px'
        },
        infoDiv: {
            // marginTop: '50px',
            // marginBottom:'50px',
            backgroundColor: theme.palette.secondary.light,
            height: 'auto',
            width: '500px',
            textAlign: 'center'
        },
    }))

    const classes = useStyles()
    return (
        <div>
            <h1 className={classes.title} >Edit Serving</h1>
            <Grid container >
                <Grid item xs={12} md={5}>
                    <img src={`http://127.0.0.1:8000${serving?.image}`}
                        className={classes.image} alt={serving?.image} ></img>
                </Grid>
                <Grid item xs={12} md={7} >
                    <div className={classes.infoDiv} >
                        <TextField label={name} onChange={(e) => setName(e.target.value)} />
                        
                    </div>
                </Grid>
            </Grid>
        </div>
    )
}

export default MyServingEditList
