import { Button, Grid, makeStyles } from '@material-ui/core'
import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { ListRestaurants } from '../actions/RestaurantActions'
import rs_bg from '../allImages/rs_bg.png'
import Restaurant from '../components/Restaurant'
function RestaurantsListScreen() {

    const [pagination, setPagination] = React.useState(1)
    const restaurantList = useSelector(state => state.restaurantList)
    const { restaurants } = restaurantList
    const restList = [
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },
        {
            name: 'aliagar',
            location: 'sagwara,sagwara,sagwara,sagwara,sagwara',

        },

    ]
    const dispatch = useDispatch()

    React.useEffect(() => {
        dispatch(ListRestaurants(pagination))
    }, [dispatch])

    const useStyles = makeStyles(theme => ({
        image: {
            position: 'fixed',
            width: '100%',
            zIndex: '-100',
            minHeight: '200px'
        },
        title: {
            position: 'relative',
            ...theme.typography.content,
            textAlign: 'center',
            fontSize: '3rem',
            top: '50px',
            color: 'black',

        }
    }))

    const classes = useStyles()

    return (
        <div>
            <img src={rs_bg} alt='bg-green' className={classes.image} ></img>
            <h1 className={classes.title} >ALL RESTAURANTS LIST</h1>
            <Grid xs={12} direction='row' container justify='space-evenly'
                style={{
                    marginTop: '70px',
                }}  >
                    <Button onClick={() => {
                    if(pagination==1)
                    dispatch(ListRestaurants(pagination))
                }} >Previous  page</Button>
                {restaurants?.results?.map(item => (
                    <Grid xs={6} direction='column' justify='center' spacing={6} md={4} item >
                        <Restaurant
                            name={item.name}
                            address={item.address}
                            city={item.city}
                            state={item.state}
                            pincode={item.pincode}
                            image={item.image}
                        />
                    </Grid>
                ))}
                <Button onClick={() => {
                    setPagination(prevState => prevState + 1)
                    dispatch(ListRestaurants(pagination))
                }} >Next page</Button>
            </Grid>
        </div>
    )
}
//next button with a state defaulted to 1, then on the button click we send a dispatch with parameter of state+1
//then we get the data of the next page and render it

//initially we dispatch the state with 1 param,then on the click of the button next...we increment the state by 1
//and get next page's data

export default RestaurantsListScreen
