import React from 'react'
import axios from 'axios'
import { useSelector } from 'react-redux'
import CustomContainer from '../../components/CustomContainer'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
function DonationRequests() {

    const [requests, setRequests] = React.useState([])
    const user = useSelector(state => state.user.userInfo)
    const myDonationRequests = async () => {
        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }
        const { data } = await axios.get('http://127.0.0.1:8000/api/restaurant/donation-requests/', config)
        setRequests(data)
    }

    React.useEffect(() => {
        myDonationRequests()
    }, [])

    const useStyles = makeStyles(theme => ({
        img: {
            width: '11vw',
        },
        container: {
            alignItems: 'center',
        },
        username:{
            ...theme.typography.title
        }
    }))

    const classes = useStyles()

    return (
        <div style={{
            display: 'flex',
            alignItems: 'center',
            flexDirection: 'column',
        }} >
            {requests.map(item => {
                console.log(item)
                return (

                    <CustomContainer style={{
                        width: '70%',
                    }} >
                        <Grid container className={classes.container} >
                            <Grid item >
                                <img src={item.image} alt='lll' className={classes.img}  ></img>
                            </Grid>
                            <Grid item >
                                <p className={classes.username} >{item.username}</p>
                            </Grid>
                        </Grid>
                    </CustomContainer>
                )
            })}
        </div>
    )
}

export default DonationRequests
