import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import axios from 'axios'
import React from 'react'
import { useSelector } from 'react-redux'

function MyServingEditList(props) {
    const [servings, setServings] = React.useState([])

    const URL = 'http://127.0.0.1:8000'
    // const store = useSelector(state => state.userReducer?.user ? state.userReducer?.user : state.userReducer?.user?.user)
    const user = JSON.parse(localStorage.getItem('userInfo'))
    const myServings = async () => {
        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }
        const { data } = await axios.get(`${URL}/api/restaurant/my-servings/`, config)
        setServings(data)
    }

    const serving = servings?.find(item => item.id == props.match.params.id)
    console.log(serving)

    React.useEffect(() => {

        myServings()

    }, [setServings])

    const useStyles = makeStyles(theme => ({
        image: {
            height: '45vh',
            width: '25vw',
            minWidth:'380px',
            marginTop: '90px',
            borderRadius: '5px',
            
        }
    }))

    const classes = useStyles()
    return (
        <Grid container >
            <Grid item xs={12} md={5}>
                <img src={`http://127.0.0.1:8000${serving?.image}`}
                    className={classes.image} alt={serving?.image} ></img>
            </Grid>

        </Grid>
    )
}

export default MyServingEditList
