import { Button, Grid, makeStyles, TextField, Typography } from '@material-ui/core'
import React from 'react'
import greenBg from '../allImages/green-bg.png'
import givingHand from '../allImages/givingHand.png'
import { useDispatch, useSelector } from 'react-redux'
import { login } from '../actions/userActions';
import {Link} from 'react-router-dom'

function LoginScreen(props) {
    const [email, setEmail] = React.useState('')
    const [password, setPassword] = React.useState('')
    const userReducer = useSelector(state => state.userReducer)
    const dispatch = useDispatch()
    const userInfo = useSelector(state=>state?.userReducer?.state?.user)
    const { user, error } = userReducer

    console.log(userInfo)
    //.state.user is of localstorage
    React.useEffect(() => {
        
    }, [dispatch,user,error,props.history,userInfo])


    const useStyles = makeStyles(theme => ({
        background: {
            width: '100%',
            height: '105%',
            
        },
        image: {
            position: 'absolute',
            width: '100%',
            height: '101%',
            zIndex: '-100',
            [theme.breakpoints.down('sm')]: {
                position:'fixed'
            }
        },
        centerItem: {
            alignItems: 'center',
            display: 'flex',
            [theme.breakpoints.down('xs')]: {
                justifyContent: 'center'
            },
            [theme.breakpoints.down('sm')]: {
                justifyContent: 'center'
            }
        },
        givingHandImage: {
            height: '300px',
            [theme.breakpoints.down('sm')]: {
                display:'none'
            }
        },
        mainHeading: {
            position: 'relative',
            ...theme.typography.content,
            fontSize: '3rem',
            textAlign: 'center',
            top: '20px',
            marginBottom: '70px',
        },
        loginBackground: {
            height: '300px',
            width: '450px',
            backgroundColor: theme.palette.primary.light,
            borderRadius: '10px',
            boxShadow: '0px 8px 0px #333333',
            [theme.breakpoints.down('sm')]: {
                marginLeft: '190px',
                marginTop: '20px',
                marginBottom: '20px',
                width:'400px'
            },
            [theme.breakpoints.down('xs')]: {
                marginLeft: '20px',
                marginTop: '20px',
                marginBottom: '20px'
            },

        },
        signinText: {
            ...theme.typography.content,
            color: 'black',
            fontSize: '2.5rem',
            textAlign: 'center',
            paddingTop: '15px'
        },
        textField: {
            backgroundColor: theme.palette.common.white,
            borderRadius: '10px',
            border: 0,
            padding: '5px',
            width: '350px',
            marginTop: '20px',
        },
        form: {
            with: '100%',
            textAlign: 'center',

        },
        button: {
            ...theme.typography.content2,
            position: 'relative',
            right: -130,
            top: 20,

        }
    }))

    const submitLoginHandler = (e) => {
        e.preventDefault()
        dispatch(login(email, password))
        if(!error){
            props.history.push('/')
        }
        
    }


    const classes = useStyles()
    return (
        <div className={classes.background} >
            <img src={greenBg} alt='bg-green' className={classes.image} ></img>
            <p className={classes.mainHeading} >WE ARE THANKFUL FOR YOUR CONTRIBUTION</p>
            {error ? (<p>{error}</p>) : null}
            <Grid direction='row' container xs={12} >
                <Grid item xs={12} md={6} >
                    <div className={classes.centerItem} >
                        <img src={givingHand} alt='givingHand' className={classes.givingHandImage} ></img>
                    </div>
                </Grid>
                <Grid item xs={12} md={6} >
                    <div className={`${classes.loginBackground}`} >
                        <p className={classes.signinText} >SIGN IN</p>
                        <form className={classes.form} onSubmit={submitLoginHandler}>
                            <TextField onChange={(e) =>
                                setEmail(e.target.value)

                            } placeholder="Username" value={email} inputProps={{ 'aria-label': 'description' }} className={classes.textField} />
                            <TextField onChange={(e) => setPassword(e.target.value)} value={password} type='password' placeholder="password" inputProps={{ 'aria-label': 'description' }} className={classes.textField} />
                            <div>
                                <Button type='submit' color='secondary' className={classes.button} variant='contained' >Sign in</Button>
                                <p>Don't have an account ?</p><Link to='signup'>Click here to create one</Link>
                            </div>
                        </form>
                    </div>
                </Grid>
            </Grid>

        </div>
    )
}

export default LoginScreen
