import { Button, Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import React from 'react'
import { useSelector } from 'react-redux'

function DonateServing(props) {
    const restaurantServings = useSelector(state => state.restaurantServings)
    const serving = restaurantServings.servings.find(item=>item.id==props.match.params.id)

    console.log(serving)
    const useStyles = makeStyles(theme => ({
        dummyImage: {
            height: '45vh',
            width: '25vw',
            minWidth: '230px',
            minHeight: '25vh',
            marginRight: 'auto',
            marginLeft: 'auto',
            marginTop: '90px',
            borderRadius: '5px'
        },
        title: {
            ...theme.typography.content,
            color: '#333333',
            textAlign: 'center',
            fontSize: '3.5rem',
            marginBottom: '30px'
        },
        infoDiv: {
            marginTop: '50px',
            marginBottom:'50px'
        },
        info: {
            ...theme.typography.title,
            fontSize: '2rem',
            textAlign: 'center',
            marginBottom: '30px',
            lineHeight: '52px',

        },
        btn: {
            ...theme.buttonDef
        },
        btnDiv: {
            display: 'flex',
            justifyContent: 'space-between',
            paddingLeft: '30px',
            paddingRight: '30px'
        }
    }))

    const classes = useStyles()
    return (
        <Grid container xs={12}>
            <Grid item direction='column' xs={12} md={5} >
                <img src={`serving?.image`} className={classes.dummyImage} >
                </img>
            </Grid>
            <Grid item direction='coulmn' xs={12} md={6} >
                <div className={classes.infoDiv} >
                    <h1 className={classes.title} >{serving?.name}</h1>

                    <p className={classes.info} >{serving?.description}</p>

                    <p className={classes.info} >Ingredients : {serving?.ingredients}</p>

                    <div className={classes.btnDiv} >
                    </div>
                </div>
            </Grid>
        </Grid>
    )
}

export default DonateServing
