import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { RestaurantServings } from '../../actions/RestaurantActions'

function MyServingsList() {

    const [servings,setServings] = React.useState([])

    const URL = 'http://127.0.0.1:8000'
    const store = useSelector(state => state.userReducer?.user ? state.userReducer?.user : state.userReducer?.user?.user)
    const user = store?.name ? store : store?.user
    console.log(user.token)
    const myServings = async () => {

        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }

        const { data } = await axios.get(`${URL}/api/restaurant/my-servings/`, config)
        console.log(data)
        setServings(data)
    }

    React.useEffect(() => {
        myServings()
    })
    const useStyles = makeStyles(theme => ({
        title: {
            ...theme.typography.content,
            textAlign: 'center',
            fontSize: '2.5rem',
            marginTop: '1rem',
            marginBottom: '30px'
        }
    }))
    const classes = useStyles()



    return (
        <div>
            <h1 className={classes.title} >My servings list</h1>
            {servings?.map(item=>{
                return(
                    <div>
                        {item.name}
                    </div>
                )
            })}
        </div>
    )
}

export default MyServingsList
