import { createSlice } from '@reduxjs/toolkit'

export const counterSlice = createSlice({
    name: 'framer-motion',
    initialState: {},
    reducers: {
        defaultPageTransition: () => {
            return {

                in: {
                    opacity: 1,
                    y: 0
                },
                out: {
                    opacity: 0,
                    y: '-100%'
                }

            }
        }
    }
})

export const { defaultPageTransition } = counterSlice.actions
export default counterSlice.reducer
