import axios from 'axios'
import { createSlice } from '@reduxjs/toolkit'

const URL = 'http://127.0.0.1:8000'

export const counterSlice = createSlice({
    name:'restaurants',
    initialState:{
        restaurants:[],
        loading:true
    },
    reducers:{
        get_products : async(state)=>{
            const data = await axios.get(URL+'/api/').then(resp=>resp.json())
            state.restaurants = data
            state.loading = false 
        }
    }
})

export const {get_products} = counterSlice.actions
export default counterSlice.reducer
