import { createStore, combineReducers, applyMiddleware } from 'redux'
import thunk from 'redux-thunk'
import { composeWithDevTools } from 'redux-devtools-extension'
import { restaurantsListReducer,
  restaurantServingsReducer,
  restaurantProfileReducer } from '../reducers/restaurantReducer'
import { userReducer,userSignupReducer,userDonateReducer } from '../reducers/userReducer'


const reducer = combineReducers({
  restaurantList: restaurantsListReducer,
  userReducer: userReducer,
  userSignup:userSignupReducer,
  restaurantServings : restaurantServingsReducer,
  userDonate:userDonateReducer,
  restaurantProfile : restaurantProfileReducer
})

const userInfoFromStorage = localStorage.getItem('userInfo') ? JSON.parse(localStorage.getItem('userInfo')) : null

const initialState = {
  userReducer :{
    user : userInfoFromStorage
  }
}

const middleWare = [thunk]

const store = createStore(reducer, initialState,
  composeWithDevTools(applyMiddleware(...middleWare)))

export default store
