import { Button, makeStyles } from '@material-ui/core'
import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { userProfileUpdate } from '../../actions/userActions'
import FormDiv from '../../components/FormDiv'

function ProfileScreen(props) {
    const [username, setUsername] = React.useState('')
    const [name, setName] = React.useState('')
    const [firstName, setFirstName] = React.useState('')
    const [lastName, setLastName] = React.useState('')
    const user = useSelector(state => state.user.userInfo)
    const dispatch = useDispatch()
    console.log(user)
    const usernameHandler = (e) => {
        setUsername(e.target.value)
    }
    const nameHandler = (e) => {
        setName(e.target.value)
    }
    const firstNameHandler = (e) => {
        setFirstName(e.target.value)
    }
    const lastNameHandler = (e) => {
        setLastName(e.target.value)
    }
    const data = [
        {
            name: 'Username',
            textFieldDisable: false,
            placeholder: user.name,
            value: user.usernme,
            onChange: usernameHandler
        },
        {
            name: 'Name',
            textFieldDisable: false,
            placeholder: name,
            value: name,
            onChange: nameHandler
        },
        {
            name: 'First Name',
            textFieldDisable: false,
            placeholder: firstName,
            value: firstName,
            onChange: firstNameHandler
        },
        {
            name: 'Last Name',
            textFieldDisable: false,
            placeholder: lastName,
            value: lastName,
            onChange: lastNameHandler
        },

    ]

    const updateProfile = () => {
        console.log('user profile update')
        dispatch(userProfileUpdate(username,firstName,lastName))
        props.histo
    }

    const useStyles = makeStyles(theme => ({
        mainDiv: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center'
        },
        heading: {
            ...theme.typography.content,
            fontSize: '2.5rem',
            textAlign: 'center'
        },
        btnLeft: {
            poaition: 'relative',
            marginRight: 'auto',
            marginLeft: '20px'
        },
    }))
    const classes = useStyles()
    return (
        <div className={classes.mainDiv} >
            <h1 className={classes.heading} >This is the profile screen</h1>
            <Button className={classes.btnLeft} variant='outlined'
                color='primary' component={Link} to='/'  >GO BACK</Button>
                <h1 >Logged in as {user.name}</h1>
            <FormDiv
                data={data}
                actionTaken={updateProfile}
                actionName='Update Profile'
            >

            </FormDiv>
        </div>
    )
}

export default ProfileScreen
