import { Button, Grid, IconButton } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import axios from 'axios'
import React from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import CustomContainer from '../../components/CustomContainer'
import DonationRequestRequests from '../../components/DonationRequestRequests'
function DonationRequests() {
    const user = useSelector(state => state.user.userInfo)
    const config = {
        headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${user.token}`,
        },
        params:{
            'type':'Donation'
        }

    }
    const [requests, setRequests] = React.useState([])
    const myDonationRequests = async () => {
        const { data } = await axios.get('http://127.0.0.1:8000/api/restaurant/donation-or-request-requests/', config)
        setRequests(data)
    }

    React.useEffect(() => {
        myDonationRequests()
    }, [])

    const useStyles = makeStyles(theme => ({
        img: {
            width: '16vw',
            minWidth: '180px',
            borderRadius: '10px',
        },
        container: {
            alignItems: 'center',
        },
        username: {
            ...theme.typography.title,
        },
        title: {
            ...theme.typography.content,
            fontSize: '2.5rem',
            marginTop: '20px'
        }
    }))

    const classes = useStyles()

    return (
        <div style={{
            display: 'flex',
            alignItems: 'center',
            flexDirection: 'column',
        }} >
            <h1 className={classes.title} >Donation Requests</h1>
            <Button color='secondary' variant='contained'
                style={{ position: 'absolute', left: '50px', top: '50px' }}
                component={Link} to={'/rest/profile/' + user.id}
            >Back</Button>
            {requests.map((item, index) => {
                const acceptDeclineHandler = async (command) => {
                    const { data } = await axios.post('http://127.0.0.1:8000/api/restaurant/accept-decline-donation-requests/', {
                        'id': item.id,
                        'serving_id': item.serving_id,
                        'quantity': item.quantity,
                        'message': command,

                    }, config)
                    console.log(data)
                    const newArr = requests.filter(e => e.id != item.id)
                    setRequests(newArr)
                }
                console.log(item)
                return (

                   <DonationRequestRequests item={item} />
                )
            })}
        </div>
    )
}

export default DonationRequests
