import React from 'react'
import axios from 'axios'
import { useSelector } from 'react-redux'
import CustomContainer from '../../components/CustomContainer'
import { Button, Grid, IconButton } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import { Link } from 'react-router-dom'
function DonationRequests() {

    const [requests, setRequests] = React.useState([])
    const user = useSelector(state => state.user.userInfo)
    const myDonationRequests = async () => {
        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }
        const { data } = await axios.get('http://127.0.0.1:8000/api/restaurant/donation-requests/', config)
        setRequests(data)
    }

    React.useEffect(() => {
        myDonationRequests()
    }, [])



    const useStyles = makeStyles(theme => ({
        img: {
            width: '16vw',
            minWidth: '180px',
            borderRadius: '10px',
        },
        container: {
            alignItems: 'center',
        },
        username: {
            ...theme.typography.title,
        },
        title:{
            ...theme.typography.content,
            fontSize:'2.5rem',
            marginTop:'20px'
        }
    }))

    const classes = useStyles()

    return (
        <div style={{
            display: 'flex',
            alignItems: 'center',
            flexDirection: 'column',
        }} >
            <h1 className={classes.title} >Donation Requests</h1>
            <Button color='secondary' variant='outlined'
             style={{position:'absolute',left:'50px',top:'50px'}}
             component={Link}
             >Back</Button>
            {requests.map(item => {
                console.log(item)
                return (

                    <CustomContainer style={{
                        width: '70%',
                        paddingTop: '20px',
                        paddingBottom: '20px',
                    }} >
                        <Grid container className={classes.container} >
                            <Grid item xs={12} md={3} >
                                <IconButton>
                                    <img src={item.image} alt='lll' className={classes.img}  ></img>
                                </IconButton>
                            </Grid>
                            <Grid item justify='center' alignItems='center' xs={12} md={2} >
                                <p className={classes.username} >{item.serving}</p>
                            </Grid>
                            <Grid item justify='center' alignItems='center' xs={12} md={2} >
                                <p className={classes.username} >{item.username}</p>
                            </Grid>
                            <Grid item justify='center' alignItems='center' xs={12} md={2} >
                                <p className={classes.username} >{item.quantity}</p>
                            </Grid>
                            <Grid item justify='space-between' alignItems='center' xs={12} md={3} >
                                <Button color='secondary' variant='outlined' >Accept</Button>
                                <Button color='primary' variant='outlined' >Decline</Button>
                            </Grid>
                        </Grid>
                    </CustomContainer>
                )
            })}
        </div>
    )
}

export default DonationRequests
