import { Grid, makeStyles, Typography,Button } from '@material-ui/core'
import React from 'react'

function Restaurant(props) {
    const useStyles = makeStyles(theme => ({
        background: {
            height: '320px',
            width: '240px',
            borderColor: theme.palette.primary.light,
            borderRadius: '10px',
            border: '3px solid',
            marginTop: '30px',
            backgroundColor: 'white',
            marginRight: 'auto',
            marginLeft: 'auto',
            textAlign: 'center',
            boxShadow: '0px 5px 5px grey'

        },
        title: {
            ...theme.typography.title,
            marginTop: '10px'
        },
        dummyImage: {
            height: '150px',
            width: '190px',
            backgroundColor: 'grey',
            marginRight: 'auto',
            marginLeft: 'auto',
            marginTop: '10px',
            borderRadius: '5px'
        },
        locationDiv: {
            wordBreak: 'break-all',
            paddingLeft: '30px',
            paddingRight: '30px'
        },
        button:{
            ...theme.buttonDef,
            position:'relative',
            right:'10px',
            backgroundColor:theme.palette.secondary.main

        }
    }))
    const classes = useStyles()
    return (
        <div className={classes.background} >
            {props.image ? (<div >
                <img src={`http://127.0.0.1:8000${props.image}`}
                    alt={props.image}
                    className={classes.dummyImage} ></img>
            </div>) : (
                <div className={classes.dummyImage} ></div>)}
            <Typography variant='h5' noWrap className={classes.title} >{props.name}</Typography>
            <div className={classes.locationDiv} >
                <Typography variant='p' className={classes.title} >{props.address},</Typography>
                <Typography variant='p' className={classes.title} >{props.city},</Typography>
                <Typography variant='p' className={classes.title} >{props.state},</Typography>
                <Typography variant='p' noWrap className={classes.title} >{props.pincode}</Typography>
                <Button className={classes.button} variant='contained' >Details</Button>
            </div>
        </div>
    )
}

export default Restaurant
