import {
    USER_LOGIN_FAIL,
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,

    USER_LOGOUT,

    USER_SIGNUP_FAIL,
    USER_SIGNUP_REQUEST,
    USER_SIGNUP_SUCCESS,

    USER_DONATE_REQUEST,
    USER_DONATE_SUCCESS,
    USER_DONATE_FAIL
} from "../constants/userConstants";


export const userReducer = (state = {
    state: {
        user: {}
    }
}, action) => {
    switch (action.type) {
        case USER_LOGIN_REQUEST:
            return {
                state: {
                    user: {
                        loading: true,
                        error: false
                    }
                }
            }
        case USER_LOGIN_SUCCESS:
            return {
                state: {
                    user: {
                        loading: false,
                        user: action.payload,
                        error: false
                    }
                }
            }
        case USER_LOGIN_FAIL:
            return {
                state: {
                    user: {
                        loading: false,
                        error: action.payload
                    }
                }
            }
        case USER_LOGOUT:
            return {}
        default:
            return {
                state
            }
    }
}

export const userSignupReducer = (state = {
    user : {}
}, action) => {
    switch (action.type) {
        case USER_SIGNUP_REQUEST:
            return {
                loading: true,
                error: false
            }
        case USER_SIGNUP_SUCCESS:
            return {
                loading: false,
                user: action.payload,
                error: false
            }
        case USER_SIGNUP_FAIL:
            return {
                loading: false,
                error: action.payload
            }
        default:
            return {
                state
            }
    }
}

export const userDonateReducer = (state = {
    serving : {}
}, action) => {
    switch (action.type) {
        case USER_DONATE_REQUEST:
            return {
                loading: true,
            }
        case USER_DONATE_SUCCESS:
            return {
                loading: false,
                serving : action.payload,
            }
        case USER_DONATE_FAIL:
            return {
                loading: false,
                error: action.payload
            }
        default:
            return {
                state
            }
    }
}
