import { Button, Grid, jssPreset, makeStyles, TextField, TextareaAutosize } from '@material-ui/core'
import axios from 'axios'
import React from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import MyServing from '../../components/MyServing'

function MyServingsList() {

    const [servings, setServings] = React.useState([])

    const URL = 'http://127.0.0.1:8000'
    const user = useSelector(state => state.user.userInfo)
    const myServings = async () => {

        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${user.token}`
            }
        }

        const { data } = await axios.get(`${URL}/api/restaurant/my-servings/`, config)
        setServings(data)
    }

    React.useEffect(() => {
        myServings()
    }, [])



    const useStyles = makeStyles(theme => ({
        title: {
            ...theme.typography.content,
            textAlign: 'center',
            fontSize: '2.5rem',
            marginTop: '1rem',
            marginBottom: '30px'
        },


    }))
    const classes = useStyles()



    return (
        <div>
            <h1 className={classes.title} >My servings list</h1>
            <Grid container justify='center' >
                {servings?.map(item => {
                        const config = {
                            headers: {
                                'Content-Type': 'application/json',
                                Authorization: `Bearer ${user.token}`
                            }
                        }
                    const deleteServingHandler = async () => {
                        const { data } = await axios.delete(`http://127.0.0.1:8000/api/restaurant/delete-serving/${item.id}`, config)
                        console.log(data)
                    }
                    return (
                        <MyServing item={item} deleteServingHandler={deleteServingHandler} />
                    )
                })}
            </Grid>
        </div>
    )
}

export default MyServingsList
