import { Button, Grid, TextField, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles'
import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { restProfile,restProfileUpdate } from '../../actions/RestaurantActions';
import TextFieldComp from '../../components/TextFieldComp';
function RestProfileScreen(props) {

    const [textFieldDisable, setTextFieldDisable] = React.useState(false)
    const [username, setUsername] = React.useState('')
    const [currentPassword, setCurrentPassword] = React.useState('')
    const [newPassword, setNewPassword] = React.useState('')
    const [newPasswordConfirm, setNewPasswordConfirm] = React.useState('')
    const [restaurantName, setRestaurantName] = React.useState('')
    const [country, setCountry] = React.useState('')
    const [city, setCity] = React.useState('')
    const [state, setState] = React.useState('')
    const [address, setAddress] = React.useState('')
    const [pincode, setPincode] = React.useState(0)

    const usernameHandler = (e) => {
        setUsername(e.target.value)
    }
    const restaurantNameHandler = (e) => {
        setRestaurantName(e.target.value)
    }
    const countryHandler = (e) => {
        setCountry(e.target.value)
    }
    const cityHandler = (e) => {
        setCity(e.target.value)
    }
    const stateHandler = (e) => {
        setState(e.target.value)
    }
    const addressHandler = (e) => {
        setAddress(e.target.value)
    }
    const pincodeHandler = (e) => {
        setPincode(e.target.value)
    }
    const currentPasswordHandler = (e) => {
        setCurrentPassword(e.target.value)
    }
    const newPasswordHandler = (e) => {
        setNewPassword(e.target.value)
    }
    const newPasswordConfirmHandler = (e) => {
        setNewPasswordConfirm(e.target.value)
    }
    const dispatch = useDispatch()
    const details = useSelector(state => state?.restaurantDetails?.details)
    const restInfo = JSON.parse(localStorage.getItem('userInfo'))
    console.log(details)
    console.log(restInfo)
    React.useEffect(() => {
        dispatch(restProfile())
        if (props.match.params.id != restInfo.id) {
            props.history.push('/rest/profile/' + restInfo.id)
        }
    }, [dispatch])

    const updateProfile = ()=>{
        dispatch(restProfileUpdate({
            'username':username,
            
        }))
    }

    const useStyles = makeStyles(theme => ({
        heading: {
            ...theme.typography.content,
            fontSize: '2.5rem',
            textAlign: 'center'
        },
        formDiv: {
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            height: 'auto',
            width: '400px',
            backgroundColor: theme.palette.secondary.light,
            marginTop: '30px',
            borderRadius: '15px',
            flexDirection: 'column',
            paddingTop: '50px',
            paddingBottom: '50px',
        },
        label: {
            ...theme.typography.title,
            fontSize: '1.5rem'
        },
        btn:{
            ...theme.buttonDef
        }
    }))
    const classes = useStyles()
    return (
        <div style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center'
        }} >
            <h1 className={classes.heading} >My servings</h1>
            <div className={classes.formDiv} >
                
                <TextFieldComp
                    class={classes.label}
                    name='Username: '
                    textFieldDisable={textFieldDisable}
                    placeholder={restInfo?.username}
                    value={username}
                    onChange={usernameHandler}
                />
                <TextFieldComp
                    class={classes.label}
                    name='Name: '
                    textFieldDisable={textFieldDisable}
                    placeholder={details?.name}
                    value={restaurantName}
                    onChange={restaurantNameHandler}
                />
                <TextFieldComp
                    class={classes.label}
                    name='Address: '
                    textFieldDisable={textFieldDisable}
                    placeholder={details?.address}
                    value={address}
                    onChange={addressHandler}
                />
                <TextFieldComp
                    class={classes.label}
                    name='City: '
                    textFieldDisable={textFieldDisable}
                    placeholder={details?.city}
                    value={city}
                    onChange={cityHandler}
                />
                <TextFieldComp
                    class={classes.label}
                    name='Country: '
                    textFieldDisable={textFieldDisable}
                    placeholder={details?.country}
                    value={country}
                    onChange={countryHandler}
                />
                <TextFieldComp
                    class={classes.label}
                    name='State: '
                    textFieldDisable={textFieldDisable}
                    placeholder={details?.state}
                    value={state}
                    onChange={stateHandler}
                />
                <TextFieldComp
                    class={classes.label}
                    name='Pincode: '
                    textFieldDisable={textFieldDisable}
                    placeholder={details?.pincode}
                    value={pincode}
                    onChange={pincodeHandler}
                />
                <TextFieldComp
                    class={classes.label}
                    name='Current Password: '
                    textFieldDisable={textFieldDisable}
                    placeholder={details?.pincode}
                    value={pincode}
                    onChange={pincodeHandler}
                />
                <Button variant='contained' color='primary'
                 className={classes.btn} onClick={updateProfile} >Update Profile</Button>
            </div>
            {/* {restaurantServings?.servings?.map(item => {
                return (
                    <Grid xs={12} container >
                        <Grid item xs={12} md={} >

                        </Grid>
                    </Grid>
                )
            })} */}
        </div>
    )
}

export default RestProfileScreen
