import React from 'react'
import { makeStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux'
import { RestaurantServings } from '../../actions/RestaurantActions';
function DonateScreen(props) {
    const dispatch = useDispatch()
    const restaurantServings = useSelector(state => state.restaurantServings)
    console.log(restaurantServings.servings)

    const { servings } = restaurantServings

    React.useEffect(() => {
        dispatch(RestaurantServings(props.match.params.id))
    }, [dispatch])

    const useStyles = makeStyles(theme => ({
        title: {
            ...theme.typography.content,
            textAlign: 'center',
            fontSize: '3rem'

        },
        main: {
            width: '15vw',
            minWidth: '180px',
            borderColor: theme.palette.primary.light,
            borderRadius: '10px',
            border: '3px solid',
            marginTop: '30px',
            backgroundColor: 'white',
            marginRight: 'auto',
            marginLeft: 'auto',
            textAlign: 'center',
            boxShadow: '0px 5px 5px grey'
        }
    }))
    const classes = useStyles()
    return (
        <div>
            <h1 className={classes.title} >Donate for the needy</h1>
            {servings?.map((item) => {
                return (
                    <div className={classes.main}>

                    </div>
                )
            })}
        </div>
    )
}

export default DonateScreen
