import { Button, Grid, makeStyles, TextField, Typography } from '@material-ui/core'
import React from 'react'
import greenBg from '../allImages/green-bg.png'
import givingHand from '../allImages/givingHand.png'


function LoginScreen() {
    const useStyles = makeStyles(theme => ({
        background: {
            width: '100%',
            height: '105%'
        },
        image: {
            position: 'absolute',
            width: '100%',
            height: '101%',
            zIndex: '-100'
        },
        centerItem: {
            // justifyContent: 'center',
            alignItems: 'center',
            display: 'flex'
        },
        givingHandImage: {
            height: '300px',

        },
        mainHeading: {
            position: 'relative',
            ...theme.typography.content,
            fontSize: '3rem',
            textAlign: 'center',
            top: '20px',
            marginBottom: '70px',
        },
        loginBackground: {
            height: '300px',
            width: '450px',
            backgroundColor: theme.palette.primary.light,
            borderRadius: '10px',
            boxShadow: '0px 8px 0px #333333',
            justifyContent: 'center'
        },
        signinText: {
            ...theme.typography.content,
            color: 'black',
            fontSize: '2.5rem',
            textAlign: 'center',
            paddingTop: '15px'
        },
        textField:{
            backgroundColor:theme.palette.common.white,
            borderRadius: '10px',
            border:0,
            padding : '5px',
            width:'350px',
            // marginBottom:'30px',
            marginTop:'20px',
        },
        form:{
            with:'100%',
            textAlign:'center',
            flexDirection:'column',
            display:'flex'
        },
        button:{
            width:'100px',
            position:'relative',
            right:-100
        }
    }))

    const classes = useStyles()
    return (
        <div className={classes.background} >
            <img src={greenBg} alt='bg-green' className={classes.image} ></img>
            <p className={classes.mainHeading} >WE ARE THANKFUL FOR YOUR CONTRIBUTION</p>
            <Grid direction='row' container xs={12} >
                <Grid item xs={12} md={6} >
                    <div className={classes.centerItem} >
                        <img src={givingHand} alt='givingHand' className={classes.givingHandImage} ></img>
                    </div>
                </Grid>
                <Grid item xs={12} md={6} >
                    <div className={`${classes.loginBackground}`} >
                        <p className={classes.signinText} >SIGN IN</p>
                        <form className={classes.form}>
                            <TextField placeholder="Username" inputProps={{ 'aria-label': 'description' }} className={classes.textField} />
                            <TextField placeholder="password" inputProps={{ 'aria-label': 'description' }} className={classes.textField} />
                            <Button color='secondary' className={classes.button} variant='contained' >Sign in</Button>
                        </form>
                    </div>
                </Grid>
            </Grid>

        </div>
    )
}

export default LoginScreen
