import { Button, Grid, makeStyles, TextField, Typography } from '@material-ui/core'
import React from 'react'
import greenBg from '../allImages/green-bg.png'
import givingHand from '../allImages/givingHand.png'


function LoginScreen() {
    const [formData,setFormData] = React.useState({
        email:'',
        password:''
    })
    const useStyles = makeStyles(theme => ({
        background: {
            width: '100%',
            height: '105%',
        },
        image: {
            position: 'absolute',
            width: '100%',
            height: '101%',
            zIndex: '-100'
        },
        centerItem: {
            alignItems: 'center',
            display: 'flex',
            [theme.breakpoints.down('xs')]: {
                justifyContent:'center'
            }
        },
        givingHandImage: {
            height: '300px',

        },
        mainHeading: {
            position: 'relative',
            ...theme.typography.content,
            fontSize: '3rem',
            textAlign: 'center',
            top: '20px',
            marginBottom: '70px',
        },
        loginBackground: {
            height: '300px',
            width: '450px',
            backgroundColor: theme.palette.primary.light,
            borderRadius: '10px',
            boxShadow: '0px 8px 0px #333333',
            [theme.breakpoints.down('xs')]: {
                marginLeft:'20px',
                marginTop:'20px',
                marginBottom:'20px'
            }
           
        },
        signinText: {
            ...theme.typography.content,
            color: 'black',
            fontSize: '2.5rem',
            textAlign: 'center',
            paddingTop: '15px'
        },
        textField: {
            backgroundColor: theme.palette.common.white,
            borderRadius: '10px',
            border: 0,
            padding: '5px',
            width: '350px',
            marginTop: '20px',
        },
        form: {
            with: '100%',
            textAlign: 'center',

        },
        button: {
            ...theme.typography.content2,
            position: 'relative',
            right: -130,
            top: 20,

        }
    }))

    const classes = useStyles()
    return (
        <div className={classes.background} >
            <img src={greenBg} alt='bg-green' className={classes.image} ></img>
            <p className={classes.mainHeading} >WE ARE THANKFUL FOR YOUR CONTRIBUTION</p>
            <Grid direction='row' container xs={12} >
                <Grid item xs={12} md={6} >
                    <div className={classes.centerItem} >
                        <img src={givingHand} alt='givingHand' className={classes.givingHandImage} ></img>
                    </div>
                </Grid>
                <Grid item xs={12} md={6} >
                    <div className={`${classes.loginBackground}`} >
                        <p className={classes.signinText} >SIGN IN</p>
                        <form className={classes.form}>
                            <TextField onChange={(e)=>setFormData({email:e.target.value})} placeholder="Username" inputProps={{ 'aria-label': 'description' }} className={classes.textField} />
                            <TextField onChange={(e)=>setFormData({password:e.target.value})} type placeholder="password" inputProps={{ 'aria-label': 'description' }} className={classes.textField} />
                            <div>
                                <Button color='secondary' className={classes.button} variant='contained' >Sign in</Button>
                            </div>
                        </form>
                    </div>
                </Grid>
            </Grid>

        </div>
    )
}

export default LoginScreen
