import React from 'react'
import { useSelector } from 'react-redux'

function DonateServing(props) {
    const restaurantServings = useSelector(state => state.restaurantServings)
    const serving = restaurantServings.servings.find(item=>item.id==props.match.params.id)

    console.log(serving)
    const useStyles = makeStyles(theme => ({
        image: {
            position: 'fixed',
            width: '100%',
            zIndex: '-100',
            height: '100%'
        },
        dummyImage: {
            height: '45vh',
            width: '25vw',
            minWidth: '230px',
            minHeight: '25vh',
            backgroundColor: 'grey',
            marginRight: 'auto',
            marginLeft: 'auto',
            marginTop: '90px',
            borderRadius: '5px'
        },
        title: {
            ...theme.typography.content,
            color: '#333333',
            textAlign: 'center',
            fontSize: '3.5rem',
            marginBottom: '30px'
        },
        infoDiv: {
            marginTop: '50px',
            marginBottom:'50px'
        },
        info: {
            ...theme.typography.title,
            fontSize: '2rem',
            textAlign: 'center',
            marginBottom: '30px',
            lineHeight: '52px',

        },
        btn: {
            ...theme.buttonDef
        },
        btnDiv: {
            display: 'flex',
            justifyContent: 'space-between',
            paddingLeft: '30px',
            paddingRight: '30px'
        }
    }))

    const classes = useStyles()
    return (
        <Grid container xs={12}>
            <img src={rsd_bg} className={classes.image} alt='rsd_bg' ></img>
            <Grid item direction='column' xs={12} md={5} >
                <div className={classes.dummyImage} >
                </div>
            </Grid>
            <Grid item direction='coulmn' xs={12} md={6} >
                <div className={classes.infoDiv} >
                    <h1 className={classes.title} >{restaurant?.name}</h1>

                    <p className={classes.info} >{restaurant?.address}, {restaurant?.city},
                    {restaurant?.state} ,{restaurant?.country}, {restaurant?.pincode}</p>

                    <p className={classes.info} >Description Description Description
                    Description Description Description Description
                    Description Description Description Description Description</p>

                    <div className={classes.btnDiv} >
                        <Button component={Link} to='/restaurants' color='primary'
                            className={classes.btn} variant='contained' >GO TO RESTAURANTS PAGE</Button>
                        <Button color='secondary' className={classes.btn} component={Link} to={`/donate/${props.match.params.id}`}
                            variant='contained' >DONATE</Button>
                    </div>
                </div>
            </Grid>
        </Grid>
    )
}

export default DonateServing
