import { createSlice } from '@reduxjs/toolkit'

export const counterSlice = createSlice({
    name: 'framer-motion',
    initialState: {
        in:{},
        out:{}
    },
    reducers: {
        defaultPageTransition: (state) => {
            // return {
            // state.in: {
            //     opacity: 1,
            //     y: 0
            // },
            // state.out: {
            //     opacity: 0,
            //     y: '-100%'
            // }    


            // }
            state.in = {
                opacity: 1,
                y: 0
            }
            state.out = {
                opacity: 0,
                y: '-100%'
            }
            
        }
    }
})

export const { defaultPageTransition } = counterSlice.actions
export default counterSlice.reducer
