import { Button, Grid, makeStyles, TextareaAutosize, TextField } from '@material-ui/core'
import React from 'react'
import axios from 'axios'

function MyServing({ item }) {
    const [enabled, setEnabled] = React.useState(true)
    const[saveBtn,setSaveBtn] = React.useState(true)
    const [name, setName] = React.useState(item.name)
    const [description, setDescription] = React.useState(item.description)
    const [ingredients, setIngredients] = React.useState(item.ingredients)
    const onEditClickHandler = () => {
        setEnabled(!enabled)
        setSaveBtn(!saveBtn)
    }

    const saveHandler = ()=>{

    }
    

    const useStyles = makeStyles(theme => ({
        main: {
            width: '13vw',
            minWidth: '250px',
            borderColor: theme.palette.primary.light,
            borderRadius: '10px',
            border: '3px solid',
            marginTop: '30px',
            backgroundColor: 'white',
            marginRight: 'auto',
            marginLeft: 'auto',
            textAlign: 'center',
            boxShadow: '0px 1px 5px grey',
            paddingBottom: '20px',
            display: 'flex',
            flexDirection: 'column'
        },
        name: {
            ...theme.typography.content,
            fontSize: '2rem',
            width: '80%',
            marginLeft: 'auto',
            marginRight: 'auto',
            marginTop: '20px'
        },
        image: {
            height: '150px',
            width: '14vw',
            minWidth: '130px',
            backgroundColor: 'grey',
            marginRight: 'auto',
            marginLeft: 'auto',
            marginTop: '10px',
            borderRadius: '5px'
        },
        description: {
            ...theme.typography.title,
            marginTop: '20px',
            width: '80%',
            marginLeft: 'auto',
            marginRight: 'auto',
        },
        span: {
            fontWeight: '900'
        },
        ing: {
            ...theme.typography.title,
            width: '80%',
            marginLeft: 'auto',
            marginRight: 'auto',
            marginTop: '20px'
        },
        btn: {
            ...theme.buttonDef,
            marginTop: '20px',
            width: '50%',
            marginLeft: 'auto',
            right: '20px'
        }
    }))

    const classes = useStyles()

    return (
        <Grid item xs={12} sm={6} md={4} direction='row' className={classes.main} >
            <img src={`http://127.0.0.1:8000${item.image}`}
                alt={item.image} className={classes.image} ></img>
            <TextField className={classes.name} value={name} disabled={enabled} onChange={(e)=>setName(e.target.value)} />
            <TextareaAutosize className={classes.description} value={description} disabled={enabled} onChange={(e)=>setDescription(e.target.value)} />
            <TextField className={classes.ing} value={ingredients} disabled={enabled} onChange={(e)=>setIngredients(e.target.value)} />
            <p>Is Vegan : </p><Switch
                            checked={state.checkedA}
                            onChange={handleChange}
                            name="checkedA"
                            inputProps={{ 'aria-label': 'secondary checkbox' }}
                        />
            {saveBtn ? (<Button variant='outlined' color='primary' onClick={onEditClickHandler}
                className={classes.btn} >Edit</Button>) : (<Button variant='contained'
                 color='secondary' onClick={onEditClickHandler}
                className={classes.btn} >Save</Button>)}
        </Grid>
    )
}

export default MyServing
