import axios from 'axios'
import { createSlice } from '@reduxjs/toolkit'

const URL = 'http://127.0.0.1:8000'

export const counterSlice = createSlice({
    name: 'restaurants',
    initialState: {
        restaurants: [],
        loading: true
    },
    reducers: {
        get_products: (state) => {
            const get_all_restaurants = ()=>{
                const data = axios.get(URL + '/api/').then(resp=>resp.data)
                return data
            }
            
            let restaurants = get_all_restaurants().then(resp=>data=resp)
            console.log(data)
            // state.restaurants = restaurants
            state.loading = false
        }
    }
})

export const { get_products } = counterSlice.actions
export default counterSlice.reducer
